import {useContext,useRef,useState} from 'react'
import { Boss } from './Puplic'
import  {A} from './Text'
export default function MiniMapping() {
    const {joky} = useContext(Boss)
    const {remove} = useContext(Boss)
    const xB = 'The opening sentence is the most important sentence of any paragraph.Busy readers focus on opening sentences and sometimes skip over subsequentsentences.'
    const x_sm = 'The opening sentence is the most important sentence of any paragraph.Busy readers focus on. …'
   
    let state1 = useRef(x_sm)
    let text1 = useRef('See More')
    let state2 = useState(x_sm)
    let text2 = useRef('See More')
    let state3 = useRef(x_sm)
    let text3 = useRef('See More')
    let state4 = useRef(x_sm)
    let text4 = useRef('See More')
    let state5 = useRef(x_sm)
    let text5 = useRef('See More')

    const u = useRef([state1.current,state2.current,state3.current,state4.current,state5.current])
    const u2 = useRef([text1.current,text2.current,text3.current,text4.current,text5.c])
    
    
    const y   = useRef(0)
    const y2  = useRef(0)
    const y3  = useRef(0)
    const y4  = useRef(0)
    const y5  = useRef(0)
 const func = (v) => {
  if(v == 1){
        if(y.current == 0){
        y.current = 1
        u.current[v-1] = x_sm
      }else{
        y.current = 0
        u2.current[v-1] = xB
      }
  }
  if(v==2){        if(y2.current == 0){
        y2.current = 1
        u.current[v-1] = x_sm

      }else{
      y2.current = 0
      u2.current[v-1] = xB

      }
}
  if(v==3){        if(y3.current == 0){
        y3.current = 1
        u.current[v-1] = x_sm

      }else{
      y3.current = 0
      u2.current[v-1] = xB

      }
}
  if(v==4){        if(y4.current == 0){
        y4.current = 1
        u.current[v-1] = x_sm

      }else{
      y4.current = 0
      u2.current[v-1] = xB

      }
}
  if(v==5){        if(y5.current == 0){
        y5.current = 1
        u.current[v-1] = x_sm

      }else{
      y5.current = 0
      u2.current[v-1] = xB

      }
}
  }

return (
    joky.map(x => 
    <li key={x.id} className='mb-3 d-flex justify-content-between align-items-center m-2'
    style={{
        listStyle : 'none'
    }}>
<img data-visualcompletion="media-vc-image" alt="May be an image of 1 person and smiling" className="x1bwycvy x193iq5w x4fas0m x19kjcj4"  src="https://scontent.fcai20-4.fna.fbcdn.net/v/t39.30808-6/366546367_3398699837056971_1173453459821253054_n.jpg?stp=cp6_dst-jpg&amp;_nc_cat=111&amp;ccb=1-7&amp;_nc_sid=09cbfe&amp;_nc_ohc=bLcQ01EkK2cAX9lAcRV&amp;_nc_ht=scontent.fcai20-4.fna&amp;oh=00_AfBLAgyf5yxbegjideI3dwGsVIc8bEPoHa3YswnfXcO7Nw&amp;oe=64EA1AE4" width={100} className='rounded-circle' />   
 
  <button className='btn bg-success' onClick={()=>remove(x.id)}>Remove</button>

 <div className='bg-warning p-2 rounded d-flex justify-content-between'style={{width:'200px'}}>
    <span className='text-light'>{u.current[x.id -1]} 
    <button onClick={() => func(x.id)} style={{
      fontSize:'14px',color:'black',cursor:'pointer',background:'transparent',
      border:'none'}}>{u2.current[x.id -1]}</button>
    </span>

 </div>

 </li> 
  ))
    }
