      <button className='btn bg-success' onClick={()=>remove(x.id)}>Remove</button>
    
     <div className='bg-warning p-2 rounded d-flex justify-content-between'style={{width:'200px'}}>
        <span className='text-light'>{u.current[x.id -1]} 
        <button onClick={() => func(x.id)} style={{
          fontSize:'14px',color:'black',cursor:'pointer',background:'transparent',
          border:'none'}}>{u2.current[x.id -1]}</button>
        </span>
    
     </div>
    
     </li> 
    
   ) )}


