import { createContext, useReducer} from "react"

const InitialState = {
    jok : [ {id:1,image:'photo',name:'Zydan',year:'20 years old'},
           {id:2,image:'photo',name:'Ahmed',year:'14 years old'},
           {id:3,image:'photo',name:'Ebrahim',year:'39 years old'},
           {id:4,image:'photo',name:'Esmael',year:'28 years old'},
           {id:5,image:'photo',name:'Ali',year:'31 years old'} ]}

export const Boss = createContext(InitialState)

const BigPatcher = (state,action) => {
    switch(action.type){
      case 'DeleteAll' : return {jok : []}
      default : return state
    }  }
    

export const Puplic =({children}) =>{
 const ok = () => {
    patcher({
        type : 'DeletAll'
})
} 
const [state,patcher] = useReducer(BigPatcher,InitialState)
  return (
    <>    
    <Boss.Provider value={{
       joky : state.jok,
       ok
    }}>
     {children}
    </Boss.Provider>
    </>
  )
}
